import React, { createContext, useState } from 'react';

const BookClubsContext = createContext();

const BookClubsProvider = ({ children }) => {
  const [bookClubs, setBookClubs] = useState([]);

  const updateBookClubs = (clubs) => {
    console.log('Updating book clubs context:', clubs);
    setBookClubs(clubs);
  };

  return (
    <BookClubsContext.Provider value={{ bookClubs, setBookClubs: updateBookClubs }}>
      {children}
    </BookClubsContext.Provider>
  );
};

export { BookClubsContext, BookClubsProvider };
