import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AppstoreOutlined, SettingOutlined, BookOutlined, UserOutlined, LogoutOutlined, PlusOutlined, ReadOutlined } from '@ant-design/icons';
import { Menu, Switch } from 'antd';
import { AuthContext } from './context/AuthContext';

const { SubMenu } = Menu;

const NavBar = () => {
  const { user, logout } = useContext(AuthContext);
  const [theme, setTheme] = useState('dark');
  const [current, setCurrent] = useState('1');

  const changeTheme = (value) => {
    setTheme(value ? 'dark' : 'light');
  };

  const onClick = (e) => {
    setCurrent(e.key);
  };

  return (
    <div>
      <Switch
        checked={theme === 'dark'}
        onChange={changeTheme}
        checkedChildren="Dark"
        unCheckedChildren="Light"
      />
      <br />
      <br />
      <Menu
        theme={theme}
        onClick={onClick}
        style={{ width: 256 }}
        selectedKeys={[current]}
        mode="inline"
      >
        <Menu.Item key="1" icon={<BookOutlined />}>
          <Link to="/book-clubs">Book Clubs</Link>
        </Menu.Item>
        <Menu.Item key="2" icon={<ReadOutlined />}>
          <Link to="/my-clubs">My Clubs</Link>
        </Menu.Item>
        <Menu.Item key="3" icon={<PlusOutlined />}>
          <Link to="/new-club">Create a Club</Link>
        </Menu.Item>
        <Menu.Item key="4" icon={<AppstoreOutlined />}>
          <Link to="/books">Library</Link>
        </Menu.Item>
        {user ? (
          <>
            {user.created_clubs && user.created_clubs.length > 0 && (
              <Menu.Item key="5" icon={<SettingOutlined />}>
                <Link to="/manage-club">Manage My Club</Link>
              </Menu.Item>
            )}
            <Menu.Item key="6" icon={<LogoutOutlined />} onClick={logout}>
              Logout
            </Menu.Item>
          </>
        ) : (
          <>
            <Menu.Item key="7" icon={<UserOutlined />}>
              <Link to="/login">Login</Link>
            </Menu.Item>
            <Menu.Item key="8" icon={<UserOutlined />}>
              <Link to="/register">Register</Link>
            </Menu.Item>
          </>
        )}
      </Menu>
    </div>
  );
};

export default NavBar;
